import React, { Component } from 'react';

class UpdatePanel extends Component {
    constructor(props) {
        super(props);
        //console.log(props)
        // Don't call this.setState() here!
        this.state =  {
            header: props.author,
            content: "Content from state...",
            status:"",
            advancedstatsupdate:"",
            stuff:"banna"
         };
      }


    getRandomQuote = (props) => {
      console.log(props.author)
      this.setState({
          header:props.author,
        status: {advancedstatsupdate:props.author,lastupdate:"today",rosterstatsupdate:"roster",defensestatsupdate:"defense",hustlestatsupdate:"hustle"},

      });

    }
     customHandleClick= async function(props) {  
        const res = await fetch('https://api.tvmaze.com/search/shows?q=power')
        const data = await res.json()
        this.setState({
            stuff:data.length
        });
        console.log(`Show data fetcheddd. Count: ${data.length}`); 
        stuff=data.length}

    componentDidMount(props) {
        const _this = this;

        console.log(_this.props)
      this.getRandomQuote(_this.props);
    }

    render() {
     const state= this.state
      return (


        <div id="quote-box" style={{'border':'3px solid black;','backgroundColor':'black'}}>
            <button onClick={this.customHandleClick()}>Tribber Update</button>
            {state.stuff}
          {state.header}
          {state.status.advancedstatsupdate}
          {state.status.lastupdate}
          {state.status.rosterstatsupdate}
          {state.status.defensestatsupdate}
          {state.status.hustlestatsupdate}
        </div>
      );
    }
  }






  export default UpdatePanel;
