import Layout from '../components/MyLayout.js'
import Link from 'next/link'
import fetch from 'isomorphic-unfetch'
import UpdatePanel from '../components/UpdatePanel'
async function customHandleClick() {  
const res = await fetch('https://api.tvmaze.com/search/shows?q=power')
const data = await res.json()
var stuff="banna"

console.log(`Show data fetcheddd. Count: ${data.length}`); 
stuff=data.length}
state={}

const Admin = (props) => (
  <Layout>

<button onClick={customHandleClick}>Tribber Update</button>
<UpdatePanel author={stuff} ></UpdatePanel>
   Admin Page stuff
    <ul>
      {props.shows.map(show => (
        <li key={show.id}>
          <Link as={`/p/${show.id}`} href={`/post?id=${show.id}`}>
            <a>{show.name}</a>
          </Link>
        </li>
      ))}
    </ul>

  </Layout>
)

Admin.getInitialProps = async function() {
  const res = await fetch('https://api.tvmaze.com/search/shows?q=batman')
  const data = await res.json()

  console.log(`Show data fetched. Count: ${data.length}`)

  return {
    shows: data.map(entry => entry.show)
  }
}

export default Admin
