import React, { Component } from 'react';

class UpdatePanel extends Component {
    constructor(props) {
        super(props);
        //console.log(props)
        // Don't call this.setState() here!
        this.state =  {
            header: props.author,
            content: "Content from state...",
            status:"",
            advancedstatsupdate:"",
            stuff:"banna"
         };
      }


    getRandomQuote = (props) => {
      console.log(props)
      this.setState({

        status: props.data,
      stuff:"banna"
      });

    }
     customHandleClick= async function(props) {  
        const res = await fetch('http://localhost:8000/heatculture/update')
        const data = await res.json()
        this.setState({
            stuff:data.length
        });
        console.log(`Show data fetcheddd. Count: ${data.length}`); 
        }

    componentDidMount(props) {
        const _this = this;

        console.log(_this.props)
      this.getRandomQuote(_this.props);
    }

    render() {
     const state= this.state
      return (


        <div id="quote-box" style={{'border':'3px solid black;','backgroundColor':'black'}}>
            <button onClick={() => { this.customHandleClick() }} >Tribber Update</button>
            Last Update:{state.status.insertionDate}

       Advanced:   {state.status.AdvancedStatsStatus} <br/>
          Hustle Stats:{state.status.HustleStatsStatus} <br/>
          Defense Stats:{state.status.DefenseStatsStatus} <br/>
          Roster Stats:{state.status.RosterStatsStatus} <br/>
          Regular Stats:{state.status.RegStatsStatus} <br/>


        </div>
      );
    }
  }






  export default UpdatePanel;
