import Layout from '../components/MyLayout.js'
import Link from 'next/link'
import fetch from 'isomorphic-unfetch'
import RandomQuoteMachine from "../components/quote"
import { Z_ASCII } from 'zlib';

const Index = (props) => (

  <Layout>

    <h1 style={{  fontSize: '3vw',border:'1px solid black',width:'100%'}}>THE HARDEST WORKING, BEST CONDITIONED, MOST PROFESSIONAL, UNSELFISH, TOUGHEST, MEANEST, NASTIEST TEAM IN THE NBA.</h1>
    <div className="container">
      Methodology: Start with Defensive Rating. subtract the percentage over the ideal weight for player's height, or add percentage under target weight. add blocks. multiply opponent second chance points and subtract from score. add rebounds. multiply charges drawn by two and add. 

      Current Rankings:<br/>
      Last Updated:


    <ul>
      {props.shows.map(show => (
        <li key={show.id}>
          <Link as={`/p/${show.id}`} href={`/post?id=${show.id}`}>
            <a>{show.PLAYER_NAME}</a>
          </Link>
        </li>
      ))}
    </ul>
    </div>
    HEAT WISDOM CORNER
    <RandomQuoteMachine></RandomQuoteMachine>
    <br/>
  </Layout>
)
var calcranking= function(data){
  var statsbyplayer={};
 var playerIDS=[];

  console.log("#########")
  data.RosterStats.map(function (x) {
    statsbyplayer[x.PLAYER_ID]={
      'RosterStats':{},
      'HustleStats':{},
      'AdvancedStats':{},
      'BasicStats':{},
      'DefenseStats':{}
    }
  playerIDs.push(x.PLAYER_ID)
  })
  // 
  //data.RosterStats.map(x=> statsbyplayer[x.PLAYER_ID]['RosterStats']=x)
  data.HustleStats.map(function (e) {

    if (playerIDS.indexOf(e.PLAYER_ID) == -1){
      statsbyplayer[e.PLAYER_ID]={
        'RosterStats':{},
        'HustleStats':{},
        'AdvancedStats':{},
        'BasicStats':{},
        'DefenseStats':{}
      }
    }


      statsbyplayer[e.PLAYER_ID]['HustleStats']=e

})


  }
Index.getInitialProps = async function() {
  const res = await fetch('http://localhost:8000/heatculture/ranking?format=json')
  const data = await res.json()

  console.log(`Show data fetched. Count: ${data.length}`)
calcranking(data);
  return {
    shows: data.HustleStats.map(entry => entry)
  }
}

export default Index
