import React, { Component } from 'react';

class UpdateMachine extends Component {

    state = {quote: ''}

    getRandomQuote = () => {

      this.setState({
        status: {advancedstatsupdate:"yep",non:"wow"},

      });
      return {advancedstats:"yep",none:"wow"};
    }

    changeQuote = () => {
      this.setState({
        quote: this.getRandomQuote()
      })    
    }

    componentDidMount() {
      this.getRandomQuote();
    }

    render() {
      const {status} = this.state;
      return (
        <div id="quote-box" style={{'border':'3px solid black;','backgroundColor':'black'}}>

          {quote.advancedstats}
          {quote.non}
        </div>
      );
    }
  }






