import React, { Component } from 'react';

class UpdatePanel extends Component {
    constructor(props) {
        super(props);
        console.log(props)
        // Don't call this.setState() here!
        this.state =  {
            header: props.author,
            content: "Content from state...",
            status:"",
            advancedstatsupdate:""
         };
      }


    getRandomQuote = () => {

      this.setState({
        status: {advancedstatsupdate:"advanced",lastupdate:"today",rosterstatsupdate:"roster",defensestatsupdate:"defense",hustlestatsupdate:"hustle"},

      });
      return status
    }

    changeQuote = () => {
      this.setState({
        status: this.getRandomQuote()
      })    
    }

    componentDidMount() {
      this.getRandomQuote();
    }

    render() {
      const {status} = this.state;
      return (
        <div id="quote-box" style={{'border':'3px solid black;','backgroundColor':'black'}}>
          {status.header}
          {status.advancedstats}
          {status.lastupdate}
          {status.rosterstatsupdate}
          {status.defensestatsupdate}
          {status.hustlestatsupdate}
        </div>
      );
    }
  }






  export default UpdatePanel;
